mapper.select:
SELECT aaa102 AS "name", aaa106 AS "pic", aaa101 AS "id"
FROM aa01
WHERE aaa101=#{id}
;

SELECT aaa102 AS "name", aaa103 AS "sex", aaa104 AS "address", aaa106 AS "pic", aaa120 AS "addition"
FROM aa01
WHERE aaa101=#{id}
;

SELECT aaa101 AS "id", aaa102 AS "name", aaa106 AS "pic", aaa108 AS "password"
FROM aa01
WHERE aaa107=#{mail}
;

mapper.update:
UPDATE aa01 SET aaa102=#{name}, aaa103=#{sex}, aaa104=#{address}, aaa120=#{addtion}
WHERE aaa101=#{id}
;

UPDATE aa01 SET aaa106=#{image}
WHERE aaa101=#{id}
;

UPDATE aa01 SET aaa108=#{newPassword}
WHERE aaa101=#{id} and aaa108=#{oldPassword}
;

mapper.select:
SELECT aaa108
FROM aa01
WHERE aaa102=#{name};
