mapper.update:
UPDATE ab01
SET aab103=#{status}
WHERE aab101=#{nid}
;

mapper.select:
SELECT count(*)
FROM ab01
where aab103=0;

SELECT count(aab101)
FROM ab01
where aab103=1 or aab103=2;

SELECT count(*)
FROM ab01
WHERE aaa101=#{uid} AND aab103=2;

SELECT COUNT(aab101)
FROM ab01
WHERE aab103=2 AND ${mdd}
;
